jQuery(document).ready(function (e) {
  e(".anps_checkbox").click(function () {
    e(".anps_checkbox").each(function () {
      e(this).prop("checked", !1);
    }),
      e(this).prop("checked", !0);
  }),
    e("select.merlin__select-control").change(function () {
      var c = e
        .map(e("select.merlin__select-control").toArray(), function (c) {
          return e(c).val();
        })
        .join(".");
      e("div#anps_demo_img").find("img").hide(),
        e("div#anps_demo_img")
          .find("img." + c)
          .show();
    });
});
